import { useRef, useEffect } from "react";
import { useLocationStore } from "../../store/useLocationStore";
import { useTrafficStore } from "../../store/useTrafficStore";

const SearchBox = () => {
  const { location, address, setLocation, setAddress } = useLocationStore();
  const { fetchTrafficUpdates } = useTrafficStore();
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (location) {
      console.log("Location updated, fetching traffic updates...");
      fetchTrafficUpdates();
    }
  }, [location]);

  const getLocation = async () => {
    if (address.trim() === "") {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const { longitude, latitude } = position.coords;
            console.log("User geolocation:", latitude, longitude); 
            setLocation({ longitude, latitude });
            fetchTrafficUpdates();
            console.log(
              `User's geolocation: Latitude: ${latitude}, Longitude: ${longitude}`
            );
          },
          () => {
            console.error("Unable to retrieve location.");
          }
        );
      } else {
        console.error("Geolocation is not supported.");
      }
    } else {
      try {
        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(
            address
          )}`
        );
        const data = await response.json();
        if (data.length > 0) {
          const { lat, lon } = data[0];
          console.log(
            `Location found for ${address}: Longitude: ${lon}, Latitude: ${lat}`
          );
          setLocation({
            longitude: parseFloat(lon),
            latitude: parseFloat(lat),
          });
          setAddress("");
          fetchTrafficUpdates();
        } else {
          console.error("Location not found for address:", address);
        }
      } catch (error) {
        console.error("Error fetching location for address:", error);
      }
    }
  };

  return (
    <div>
      <input
        ref={inputRef}
        type="text"
        placeholder="Search for a location..."
        value={address}
        onChange={(e) => setAddress(e.target.value)}
      />
      <button onClick={getLocation}>Get Location</button>
      <p>
        {location
          ? `Latitude: ${location.latitude}, Longitude: ${location.longitude}`
          : "No location available"}
      </p>
    </div>
  );
};

export default SearchBox;
