import { useState, useRef, useEffect } from "react";
import { useLocationStore } from "../../store/useInputStore";
import { useTrafficStore } from "../../store/useTrafficStore";

const SearchBox = () => {
  const { location, address, setLocation, setAddress } = useLocationStore();
  const { fetchTrafficUpdates } = useTrafficStore();
  const inputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {{
      fetchTrafficUpdates(); 
    }
  }, [location]);

  const getLocation = async () => {
    if (address.trim() === "") {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const latitude = position.coords.latitude;
            const longitude = position.coords.longitude;
            setLocation(`Latitude: ${latitude}, Longitude: ${longitude}`);
          },
          () => {
            setLocation("Unable to retrieve location.");
          }
        );
      } else {
        setLocation("Geolocation is not supported by this browser.");
      }
    } else {
      try {
        const response = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(address)}`
        );
        const data = await response.json();
        if (data.length > 0) {
          const { lat, lon } = data[0];
          setLocation(`Latitude: ${lat}, Longitude: ${lon}`);
          setAddress("");
        } else {
          setLocation("Location not found.");
        }
      } catch {
        setLocation("Error fetching location.");
      }
    }
  };

  return (
    <div>
      <input ref={inputRef} type="text" placeholder="Search for a location..." value={address} onChange={(e) => setAddress(e.target.value)} />
      <button onClick={getLocation}>Get Location</button>
      <p>{location}</p>
    </div>
  );
};

export default SearchBox;
