import { useEffect, useState } from "react";
import { useTrafficStore } from "../../store/useTrafficStore";
import styles from "../TrafficUpdates/TrafficUpdates.module.css";

type CardProps = {
  title: string;
};

const TrafficUpdates = ({ title }: CardProps) => {
  const { trafficUpdates, fetchTrafficUpdates } = useTrafficStore();

  useEffect(() => {
    fetchTrafficUpdates();
  }, []);

  return (
    <div>
    <h2>{title}</h2>
    {trafficUpdates.length > 0 ? (
      <div className={`${styles.container}bg-green-500 text-white rounded-2xl text-xs`}>
        {trafficUpdates.map((update, index) => (
          <div key={index} className=" p-4 flex gap-4 items-center">

            {/* ðŸ“Œ VÃ¤nsterkolumn: Text (70%) */}
            <div className="bg-teal-400 p-2 flex-1">
              <p><strong>Allvarlighetsgrad:</strong> {update.SeverityText}</p>
              <p className="font-bold">{update.Message}</p>
              <p><strong>VÃ¤g:</strong> {update.RoadNumber}</p>
              <p><strong>Tid:</strong> {new Date(update.CreationTime).toLocaleString()}</p>
              <p><strong>Plats:</strong> {update.LocationDescriptor}</p>
            </div>

            {/* ðŸ“Œ HÃ¶gerkolumn: Bild (30%) */}
            <div className="bg-teal-500 p-4 flex justify-center items-center rounded-xl flex-none w-1/3">
              <img 
                src="/assets/map.png"  
                alt="Traffic Update"
                className="rounded-lg w-full h-auto"
              />
            </div>

          </div>
        ))}
      </div>
    ) : (
      <p>No traffic updates available.</p>
    )}
  </div>
  );
};

export default TrafficUpdates;
